import React, { Component } from "react";
import API from "../utils/API";
import Container from "../components/Container";
import QueryForm from "../components/QueryForm"
import Row from "../components/Row";
import Col from "../components/Col";
import Hero from "../components/Hero"
import AlistHeader from "../components/Alist-Header"
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List";
import query from 'query-string'
import ProfileModal from "../components/ProfileModal"


class Query extends Component {
    state = {
        profiles: null,
        name: "",
        profileDetail: null,
        location: "",
        gender: "",
        instrument: "",
        style: "",
        showProfiles: false,
        showProfileModal: false
    }

    componentDidMount() {
        this.loadProfiles();
    }


    loadProfiles = (req, res) => {
        API.getProfiles({
            name: this.state.name,
            location: this.state.location,
            gender: this.state.gender, 
            instrument: this.state.instrument,
            style: this.state.style,

        })
            .then(res => {
                //if(instrument === instrumentKey && style === styleKey){

                this.setState({ profiles: res.data })
                // }
            }).catch(err => console.log(err));
    };

    //create on click for get profile 
    //grab the id of that field 
    profileModal = (e, id) => {
        e.preventDefault();
        API.getModalProfile(id).then(res => {
            this.setState({ showProfileModal: true, profileDetail: res.data })
        }).catch(err => console.log(err))
        console.log('the id ', id);
    }

    handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };


    handleFormSubmit = event => {
        event.preventDefault();
        this.setState({
            query: "",
            errors: {},
            showProfiles: true
        });

        //initialize variables for the query form inputs 
        // const instrumentKey = document.getElementById('instrument-input').value;
        // const styleKey = document.getElementById('style-input').value;
       console.log('the state now ', this.state);

        // REACT ROUTER CODE TO SET QUERY
       // let query = "?instrument=" + this.state.instrument + "&style=" + this.state.style;
        //console.log(this.props.history)
        this.props.history.push(query)

        API.getProfiles({
            instrument: this.state.instrument,
            style: this.state.style, 
            gender: this.state.gender
        })
            .then(res => this.setState({ profiles: res.data }))
            .catch(err => console.log(err));

    };

    render() {
        console.log('this.state', this.state);
        const { errors, style, links, age, about, experience, location, name, imageUrl, instrument, contact, gender, profiles, showProfiles, showProfileModal, profileDetail} = this.state;
        return (
            <div>
               <ProfileModal name={name}age={age} location={location}></ProfileModal>
                <Container style={{ marginTop: 30 }}>
                    <AlistHeader backgroundImage="https://images.pexels.com/photos/755416/pexels-photo-755416.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260">
                        <Row>

                        </Row>
                        <Col size="md-12">
                                <QueryForm handleInputChange={this.handleInputChange} />
                            </Col>
                        <Row>
                            <Col size="md-12">
                                <button
                                    id="profileSearch"
                                    className="btn btn-info"
                                    onClick={this.handleFormSubmit}
                                >Search</button>
                            </Col>




                        </Row>
                  </AlistHeader>
                    <Row>

                        <Col className="profile-table text-light" size="xs-12">


                            {(profiles && showProfiles) &&
                                <table className="table table-striped" placeholder="Results" id="resTable">

                                    <thead>
                                        <tr>
                                            <th scope="col">Name</th>
                                            <th scope="col">Location</th>
                                            <th scope="col">Instrument</th>
                                            <th scope="col">Style</th>
                                            <th scope="col">Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {profiles.map((profile, index) =>
                                            <tr key={profile._id}>

                                                <td>
                                                    <strong>
                                                        {profile.name}
                                                    </strong>
                                                </td>
                                                <td>
                                                    <strong>
                                                        {profile.location}
                                                    </strong>
                                                </td>
                                                <td>
                                                    <strong>
                                                        {profile.instrument}
                                                    </strong>
                                                </td>
                                                <td>
                                                    <strong>
                                                        {profile.style}
                                                    </strong>
                                                </td>
                                                <td>
                                                    <button

                                                        onClick={(e, profile_id) => this.profileModal(e, profile._id)}
                                                        data-toggle="modal"
                                                        data-target="#exampleModal"
                                                    >
                                                        Open Profile
                                                </button>
                                                </td>
                                            </tr>

                                        )}
                                    </tbody>
                                </table>

                            }


                        </Col>
                    </Row>

                </Container>
            </div>
        )
    }
}
export default Query;
