import React, { Component } from "react";
import Container from "../Container";
import Row from "../Row";
import API from "../../utils/API"
import Col from "../Col";


class Login extends Component {
    state = {
        email: "",
        password: "",

    }



    componentDidMount() {
        this.loadProfiles();
    }


    loadProfiles = (req, res) => {
        API.getProfiles({
            name: this.state.name,
            location: this.state.location,
            gender: this.state.gender,
            instrument: this.state.instrument,
            style: this.state.style,

        })
            .then(res => {
                //if(instrument === instrumentKey && style === styleKey){

                this.setState({ profiles: res.data })
                // }
            }).catch(err => console.log(err));
    };

    // //create on click for get profile 
    // //grab the id of that field 
    // profileModal = (e, id) => {
    //     e.preventDefault();
    //     API.getModalProfile(id).then(res => {
    //         this.setState({ showProfileModal: true, profileDetail: res.data })
    //     }).catch(err => console.log(err))
    //     console.log('the id ', id);
    // }




    handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    handleFormSubmit = event => {
        event.preventDefault();

        this.setState({
            email: "",
            password: "",
            errors: {},

        });



        //set state to user's profile and get it from API 
        window.location.pathname = "/myAccount"
    }
    render() {
        const { errors, style, links, age, about, experience, location, name, image, instrument, contact, gender, profiles, showProfiles, showProfileModal, profileDetail } = this.state;
        return (
            <div>
                <Container>





                    <form>
                        <div class="form-group text-white">
                            <label for="exampleFormControlInput1">Enter Email</label>
                            <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
                        </div>
                        <div class="form-group text-white">
                            <label for="exampleFormControlInput1">Enter Password</label>
                            <input type="password" class="form-control" name ="password"id="exampleFormControlInput1" placeholder="name@example.com" />
                        </div>
                        <button
                            id="profileSearch"
                            className="btn btn-info"
                            onClick={this.handleFormSubmit}

                            data-toggle="modal"
                            data-target="#exampleModal"
                        >Log In</button>

                    </form>
                </Container>
            </div>
        )
    }
}


export default Login;
